import axios from "axios";
import React, {useState, useEffect} from "react";

/*async function EvoTest(id) {
    const specUrl = `https://pokeapi.co/api/v2/pokemon/${id}`;
    const res = await axios.get(specUrl);
   return res.data.sprites.front_default
}*/

function EvoTest(id) {
    const [evo, setEvo] = useState([]);
    const evoSite = `https://pokeapi.co/api/v2/pokemon/${id}`
    async function GetEvo() {
        const res = await axios.get(evoSite);
        setEvo(poke => {
            poke = [...poke, res.data]
            return poke
        });
    }

    useEffect(() => {
        GetEvo();
    }, [evoSite])

    const evoData = evo[0];
    return (
        <img src={evoData.sprites.front_default} alt=""/>
    )
}

export default EvoTest;
