import axios from "axios";

async function GetPoke(pokeParam) {
    setIsLoading(true);
    const res = await axios.get(pokeParam);
    setPokemonData(poke => {
        poke = [...poke, res.data]
        return poke
    });
    setIsLoading(false);
}

export default GetPoke;
