import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import CapName from "../Hooks/Capitalize";


function Pokedex() {
    const {name} = useParams();
    const [pokemonData, setPokemonData] = useState([]);
    const currentDex = `https://pokeapi.co/api/v2/pokemon-species/${name}`;
    const [isLoading, setIsLoading] = useState(true);

    async function GetPoke() {
        setIsLoading(true);
        const res = await axios.get(currentDex);
        setPokemonData(poke => {
            poke = [...poke, res.data]
            return poke
        });
        setIsLoading(false);
    }

    useEffect(() => {
        GetPoke();
    }, [currentDex])

    if (isLoading) return ("Page Loading...")

    const pokeData = pokemonData[0];
    console.log(pokeData)

    return (
        <div>
            <div>
            {pokeData.flavor_text_entries[0].flavor_text}
            </div>

            <Link to= "/">Go Home </Link>
        </div>


    )

}

export default Pokedex;
