import React, { useEffect, useState } from "react";
import axios from "axios";
import RandomButton from "./RandomButton";

function Partner({ currentUser }) {
   const [randomId, setRandomId] = useState([9]);
   const pokeId = `https://pokeapi.co/api/v2/pokemon/${randomId}`;
   const [currentPoke, setCurrentPokemon] = useState([]);
   const [nextPoke, setNextPoke] = useState();
   const [isLoading, setIsLoading] = useState(true);


   async function GetPoke() {
    setIsLoading(true);
    const res = await axios.get(pokeId);
    setCurrentPokemon(poke => {
            poke = [...poke, res.data]
            return poke
        });
    setNextPoke(Math.floor(Math.random() * 1026));
    setIsLoading(false);
   };

   useEffect(() => {
    GetPoke();
   }, [pokeId]);

   const pokeData = currentPoke[0];

   function handleClick() {
    setCurrentPokemon([])
    setRandomId(nextPoke)
    console.log(currentUser.username, pokeData.name);
   }

   //const pokeName = pokeData.name;

   if (isLoading) return ("Loading...");


   return(
    <div className="poke-p">
        <RandomButton handleClick={handleClick}/>
        <div className="user-result">
            <p className="user-p">{currentUser.username} sent out random name</p>
        </div>
    </div>
   )

}

export default Partner;
