import React, { useEffect, useState } from "react";
import axios from "axios";

function Partner({ currentUser }) {
   const [currentPoke, setCurrentPokemon] = useState([]);
   const [randomId, setRandomId] = useState([]);
   const pokeId = `https://pokeapi.co/api/v2/pokemon/${randomId}`;
   const [isLoading, setIsLoading] = useState(true);

   function handleClick(evt) {
    evt.preventDefault();
    setRandomId(Math.floor(Math.random() * 1026));
    console.log(`${currentUser.username} sent out ${pokeData.name}!`)
    console.log(randomId);
    console.log(pokeId);


   async function GetPoke() {
    setIsLoading(true);
    const res = await axios.get(pokeId);
    setCurrentPokemon(poke => {
            poke = [...poke, res.data]
            return poke
        });
    setIsLoading(false);
   };

   useEffect(() => {
    GetPoke();
   }, [pokeId]);

   const pokeData = currentPoke[0];

    return (
        <div className="user-result">
            <p className="user-p">
                {currentUser.username} sent out {pokeData.name}!
            </p>
        </div>
    )
   }

   if (isLoading) return ("Loading...");


   return(
    <div className="partner-results">
        <button className="change-p" onClick={handleClick}>Make Scenario</button>
    </div>
   )

}

export default Partner;
