import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";



function PokeDetail() {
    const {name} = useParams();
    const [pokemonData, setPokemonData] = useState([]);
    const currentPoke =`https://pokeapi.co/api/v2/pokemon/${name}`;
    const [isLoading, setIsLoading] = useState(true);
    const evolution = `https://bulbapedia.bulbagarden.net/wiki/${name}_(Pok%C3%A9mon)#Evolution`;
    const pokedex = `https://bulbapedia.bulbagarden.net/wiki/${name}_(Pok%C3%A9mon)#Pok%C3%A9dex_entries`;
    const baseStats = `https://bulbapedia.bulbagarden.net/wiki/${name}_(Pok%C3%A9mon)#Base_stats`;

    async function getPoke() {
        setIsLoading(true);
        const res = await axios.get(currentPoke);
        setPokemonData(poke => {
            poke = [...poke, res.data]
            return poke
        });
        setIsLoading(false);
    }

    useEffect(() => {
        getPoke();
    }, [currentPoke])

    if (isLoading) return ("Page Loading...")

    const pokeData = pokemonData[0];
    const pokeName = pokeData.name;
    const firstLetter = pokeName.charAt(0)
    const firstCap = firstLetter.toUpperCase();
    const remaining = pokeName.slice(1);
    const capName = firstCap + remaining;

    return (
        <div className="pokemon-container">
            <h1 className="poke-header">{capName}</h1>
            <div className="poke-picture">
               <img className="poke-sprite" src={pokeData.sprites.other.showdown.front_default} alt=""/>
               <ul>
                <li><a href={evolution}>Evolution</a></li>
                <li><a href={pokedex}>Pokedex Entries</a></li>
                <li><a href={baseStats}>Base Stats</a></li>
               </ul>

            </div>



            <Link className="home-link" to="/">Go Back</Link>
        </div>
    )
}

export default PokeDetail;
