import React, { useEffect, useState } from "react";
import RouteList from "./Routes/RouteList";
import UserApi from "./api/api";
import UserContext from "./UserContext";
import uselocalStorage from "./hooks/localStorage";
import jwt from "jsonwebtoken";

export const TOKEN_STORAGE_ID = "my-token"

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = uselocalStorage(TOKEN_STORAGE_ID);

  useEffect(function loadUserInfo() {
    async function getCurrentUser() {
      if(token) {
        try {
          let { username } = jwt.decode(token);
          UserApi.token = token;
          let currentUser = await UserApi.getCurrentUser(username);
          setCurrentUser(currentUser);
        } catch (err) {
          console.error(err);
          setCurrentUser(null);
        }
      }
    }
    getCurrentUser();
  }, [token]);

  async function signup(signupData) {
    try {
      let token = await UserApi.signup(signupData);
      setToken(token);
      return { success: true};
    } catch (err) {
      console.error(err);
      return { success: false, err};
    }
  }

  async function login(loginData) {
    try {
      let token = await UserApi.login(loginData);
      setToken(token);
      return { success: true };
    } catch (err) {
      console.error(err);
      return { success: false, err};
    }
  }

  return (
    <div>
      <h1 className="page-header">Welcome to Your Pokedex</h1>
      <UserContext.Provider value={{ currentUser, setCurrentUser}}>
        <RouteList login={login} signup={signup}/>
        </UserContext.Provider>
    </div>
  );
}

export default App;
