import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import CapName from "../Hooks/capitalize";
import backGroundTest from "../Hooks/background";
import "./pokepage.css";


function PokeDetail() {
    const {name} = useParams();
    const [pokemonData, setPokemonData] = useState([]);
    const currentPoke =`https://pokeapi.co/api/v2/pokemon/${name}`;
    const [isLoading, setIsLoading] = useState(true);
    const evolution = `https://bulbapedia.bulbagarden.net/wiki/${name}_(Pok%C3%A9mon)#Evolution`;
    const baseStats = `https://bulbapedia.bulbagarden.net/wiki/${name}_(Pok%C3%A9mon)#Base_stats`;
    const currentDex = `https://pokeapi.co/api/v2/pokemon-species/${name}`;
    const [dex, setDex] = useState([]);

    async function GetPoke() {
        setIsLoading(true);
        const res = await axios.get(currentPoke);
        setPokemonData(poke => {
            poke = [...poke, res.data]
            return poke
        });
        setIsLoading(false);
    }

    async function GetDex() {
        setIsLoading(true);
        const res = await axios.get(currentDex);
        setDex(poke => {
            poke = [...poke, res.data]
            return poke
        });
        setIsLoading(false);
    }

    useEffect(() => {
        GetPoke();
    }, [currentPoke])

    useEffect(() => {
        GetDex();
    }, [currentDex])

    if (isLoading) return ("Page Loading...")

    const pokeData = pokemonData[0];
    const dexData = dex[0];
    const pokeName = pokeData.name;


    console.log(pokeData);
    console.log(pokeData.types[0].type.name);

    backGroundTest(pokeData);



    return (
        <div className="pokemon-container">
            <h1 className="poke-header">No. {pokeData.id}: {CapName(pokeName)}</h1>
            <div className="poke-picture">
               <img className="poke-sprite" src={pokeData.sprites.front_default} alt=""/>
            </div>   
            <div className="par-links">
                <h3 className="height-weight">Height: {pokeData.height}m</h3>
                <h3 className="height-weight">Weight: {pokeData.weight}kg</h3>   
               <p className="poke-par"> {dexData.flavor_text_entries[0].flavor_text}</p>
               <ul>
                <li><a href={baseStats}>Base Stats</a></li>
                <li><a href={evolution}>Evolution</a></li>

               </ul>

            </div>



            <Link className="home-link" to="/">Go Back</Link>
        </div>
    )
}

export default PokeDetail;
